<?php

class GURCAPTCHA_install {

    private static $instance = null;
    private $wpdb, $tablename;

    private function __construct() {
        global $wpdb;
        $this->wpdb = $wpdb;
        $this->tablename = $this->wpdb->prefix . 'GURCAPTCHA_Images';
    }

    /**
     * Initialize current class
     * @return GURCAPTCHA_install
     */
    static function getInstance() {
        if (is_null(self::$instance))
            self::$instance = new GURCAPTCHA_install();
        return self::$instance;
    }



    private function _createTables() {
        require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
        dbDelta("CREATE TABLE $this->tablename (
            id mediumint(9) NOT NULL AUTO_INCREMENT,
            image BLOB NOT NULL,
            mime varchar(25) NOT NULL,
            `order` int NOT NULL,
          primary key(id));");
    }

    private function _insertDefaultData() {
        $this->wpdb->query("
                INSERT INTO `wp_GURCAPTCHA_Images` (`id`, `image`, `mime`, `order`) VALUES (NULL, , '', '0'),
                (NULL, , '', '1')");
        $this->wpdb->query("
                INSERT INTO `wp_GURCAPTCHA_Images` (`id`, `image`, `mime`, `order`) VALUES     
                (NULL, , '', '2')");
        $this->wpdb->query("
                INSERT INTO `wp_GURCAPTCHA_Images` (`id`, `image`, `mime`, `order`) VALUES     
          (NULL, , '', '3')");
        $this->wpdb->query("
                INSERT INTO `wp_GURCAPTCHA_Images` (`id`, `image`, `mime`, `order`) VALUES               
                (NULL, , '', '4');	"
        );
    }

    /**
     * @param String $message
     */
    private function _error($message) {
            echo '<strong>' . $message . '</strong>';
            exit;
    }
    
    private function _validate_minimum_requirements(){
        // PHP 5.4 required
        if(version_compare(PHP_VERSION, '5.4', '<'))
                $this->_error ('Sorry but you PHP version must be 5.4 or higher.');
        // GD required
        if(!(extension_loaded('gd') && function_exists('gd_info')))
            $this->_error ('Sorry but you GD extension must be available.');
    }

    public function activate() {
        // Validate minimum requirement
        $this->_validate_minimum_requirements();
        // Use the wordpress prefix before table_name
        if (!tableExist($this->tablename)) {
            $this->_createTables();
            $this->_insertDefaultData();
        }
    }

    public function deactivate() {
        if (tableExist($this->tablename)) {
            $this->wpdb->query("DROP TABLE $this->tablename");
            $this->wpdb->query("DROP TABLE ". $this->wpdb->prefix.'GURCAPTCHA_Session');
        }
    }

}

?>